import React, { useEffect, useState } from 'react'
import { Tabs, Image, Button, List, Skeleton, Switch, Card, Typography } from 'antd'
import { ReloadOutlined } from '@ant-design/icons'
import { LazyLoadImage } from 'react-lazy-load-image-component'
import Router from 'next/router'
import ApiMovie from 'api/movie'
import styles from './styles.module.scss'

const baseUrlImg = `https://image.tmdb.org/t/p/original`
const perPage = 20

export default function Home() {
  const [initLoading, setInitLoading] = useState(true)
  const [loading, setLoading] = useState(false)
  const [refreshing, setRefreshing] = useState(false)
  const [data, setData] = useState([])
  const [data1, setData1] = useState([])
  const [list, setList] = useState([])
  const [list1, setList1] = useState([])
  const [page, setPage] = useState(1)
  const [page1, setPage1] = useState(1)
  const [curTabKey, setCurTabKey] = useState(1)
  const [grid, setGrid] = useState({
    gutter: 16,
    xs: 1,
    sm: 2,
    md: 4,
    lg: 4,
    xl: 5,
  })

  useEffect(() => {
    ApiMovie.getListNowPlaying(1).then((res) => {
      setInitLoading(false)
      setData(res.results)
      setList(res.results)
      setPage(res.page + 1)
    })
  }, [])

  const onChangeTab = (key) => {
    setCurTabKey(key)
    if (key == 1) {
      if (!data.length === 0) {
        ApiMovie.getListNowPlaying(1).then((res) => {
          setInitLoading(false)
          setData(res.results)
          setList(res.results)
          setPage(res.page + 1)
        })
      }
    } else {
      if (data1.length === 0) {
        setInitLoading(true)
        ApiMovie.getListTopRated(1).then((res) => {
          setInitLoading(false)
          setData1(res.results)
          setList1(res.results)
          setPage1(res.page + 1)
        })
      }
    }
  }

  const onLoadMore = () => {
    setLoading(true)
    setList(
      data.concat(
        [...new Array(perPage)].map(() => ({
          loading: true,
          poster_path: {},
          title: {},
          description: {},
        }))
      )
    )

    ApiMovie.getListNowPlaying(page).then((res) => {
      const newData = data.concat(res.results)
      setData(newData)
      setList(newData)
      setLoading(false)
      setPage(res.page + 1)
      window.dispatchEvent(new Event('resize'))
    })
  }

  const onLoadMore1 = () => {
    setLoading(true)
    setList1(
      data1.concat(
        [...new Array(perPage)].map(() => ({
          loading: true,
          poster_path: {},
          title: {},
          description: {},
        }))
      )
    )

    ApiMovie.getListTopRated(page1).then((res) => {
      const newData = data1.concat(res.results)
      setData1(newData)
      setList1(newData)
      setLoading(false)
      setPage1(res.page + 1)
      window.dispatchEvent(new Event('resize'))
    })
  }

  const loadMore =
    !initLoading && !loading ? (
      <div
        style={{
          textAlign: 'center',
          marginTop: 12,
          height: 32,
          lineHeight: '32px',
        }}
      >
        <Button onClick={onLoadMore}>loading more</Button>
      </div>
    ) : null

  const loadMore1 =
    !initLoading && !loading ? (
      <div
        style={{
          textAlign: 'center',
          marginTop: 12,
          height: 32,
          lineHeight: '32px',
        }}
      >
        <Button onClick={onLoadMore1}>loading more</Button>
      </div>
    ) : null

  const Item = ({ poster_path, overview, title }) =>
    !grid ? (
      <List.Item.Meta
        avatar={
          <LazyLoadImage
            placeholderSrc="data:image/png;base64,"
            style={{ height: 100, objectFit: 'cover' }}
            width={200}
            src={baseUrlImg + poster_path}
          />
        }
        title={title}
        description={overview}
      />
    ) : (
      <Card
        hoverable
        cover={
          <LazyLoadImage
            alt={title}
            placeholderSrc="data:image/png;base64,"
            src={baseUrlImg + poster_path}
            width="100%"
            height={330}
          />
        }
      >
        <Card.Meta
          title={
            <Typography.Text style={{ width: '100%' }} ellipsis={{ tooltip: title }}>
              {title}
            </Typography.Text>
          }
          description={
            <Typography.Paragraph ellipsis={{ rows: 2, tooltip: overview }}>{overview}</Typography.Paragraph>
          }
        />
      </Card>
    )

  const onChangeView = (checked) => {
    if (!checked) {
      setGrid({
        gutter: 16,
        xs: 1,
        sm: 2,
        md: 4,
        lg: 4,
        xl: 5,
      })
    } else {
      setGrid(false)
    }
  }

  const onRefresh = () => {
    setRefreshing(true)
    if (curTabKey == 1) {
      ApiMovie.getListNowPlaying(1).then((res) => {
        setInitLoading(false)
        setData(res.results)
        setList(res.results)
        setPage(res.page + 1)
        setRefreshing(false)
      })
    } else {
      ApiMovie.getListTopRated(1).then((res) => {
        setInitLoading(false)
        setData1(res.results)
        setList1(res.results)
        setPage1(res.page + 1)
        setRefreshing(false)
      })
    }
  }

  const onClickItem = (item) => {
    Router.push({
      pathname: '[id]',
      query: { id: item.id },
    })
  }

  return (
    <>
      <Switch
        checkedChildren="List view"
        unCheckedChildren="Grid view"
        defaultChecked={false}
        onChange={onChangeView}
      />
      <Tabs
        defaultActiveKey="1"
        onChange={onChangeTab}
        tabBarExtraContent={
          <Button type="primary" icon={<ReloadOutlined />} loading={refreshing} onClick={onRefresh}>
            Refresh
          </Button>
        }
      >
        <Tabs.TabPane tab="Now Playing" key="1">
          <List
            className="demo-loadmore-list"
            grid={grid}
            loading={initLoading}
            itemLayout="horizontal"
            loadMore={loadMore}
            dataSource={list}
            renderItem={(item) => (
              <List.Item className={styles.listItem} onClick={() => onClickItem(item)}>
                <Skeleton avatar title={false} loading={item.loading} active>
                  <Item {...item} />
                </Skeleton>
              </List.Item>
            )}
          />
        </Tabs.TabPane>
        <Tabs.TabPane tab="Top Rated" key="2">
          <List
            className="demo-loadmore-list"
            grid={grid}
            loading={initLoading}
            itemLayout="horizontal"
            loadMore={loadMore1}
            dataSource={list1}
            renderItem={(item) => (
              <List.Item className={styles.listItem} onClick={() => onClickItem(item)}>
                <Skeleton avatar title={false} loading={item.loading} active>
                  <Item {...item} />
                </Skeleton>
              </List.Item>
            )}
          />
        </Tabs.TabPane>
      </Tabs>
    </>
  )
}
